import React, { useState } from 'react';
import { QrReader } from 'react-qr-reader';
import './../styles.css'; // Archivo de estilos para el componente

function ScanQR() {
  const [qrData, setQrData] = useState(null); // Estado para almacenar los datos del QR

  const handleScan = (data) => {
    if (data) {
      setQrData(data); // Almacena los datos escaneados
    }
  };

  const handleError = (err) => {
    console.error('Error al escanear el código QR:', err);
  };

  return (
    <div className="scan-qr-container">
      <h2>Escanear Código QR</h2>
      <QrReader
        delay={300}
        onError={handleError}
        onScan={handleScan}
        style={{ width: '100%' }}
      />
      {qrData && (
        <div className="qr-result">
          <h3>Datos del QR:</h3>
          <p>{qrData}</p>
        </div>
      )}
    </div>
  );
}

export default ScanQR;
