import React, { useEffect, useState, useContext } from 'react';
import './../styles.css'; // Archivo de estilos para el componente
import Toolbar from './Toolbar';
import { useNavigate } from 'react-router-dom'; // Importar useNavigate para la navegación
import { NotificationsContext } from '../context/NotificationsContext';

function Notifications() {
    const [alert, setAlert] = useState(null); // Estado para almacenar una única alerta válida
    const [error, setError] = useState(''); // Estado para manejar errores
    const navigate = useNavigate(); // Hook para la navegación
    const { setUnreadCount } = useContext(NotificationsContext); // Acceder al contexto

    useEffect(() => {
        // Función para obtener las alertas desde el servicio
        const fetchAlerts = async () => {
            try {
                const response = await fetch('http://127.0.0.1:9000/alertas');
                if (!response.ok) {
                    throw new Error('Error al obtener las alertas');
                }
                const data = await response.json();

                if (data.success && Array.isArray(data.data)) {
                    // Filtrar la primera alerta con datos de ubicación válidos
                    const validAlert = data.data.find(alert => 
                        alert.ubicacion &&
                        alert.ubicacion.ciudad &&
                        alert.ubicacion.latitud &&
                        alert.ubicacion.longitud &&
                        alert.ubicacion.pais &&
                        alert.ubicacion.region
                    );

                    if (validAlert) {
                        setAlert(validAlert); // Almacenar la alerta válida
                        setUnreadCount(0); // Marcar las notificaciones como leídas
                    } else {
                        setError('No hay alertas con datos de ubicación válidos');
                    }
                } else {
                    setError('No se pudieron cargar las alertas');
                }
            } catch (err) {
                console.error('Error al obtener las alertas:', err);
                setError('No se pudieron cargar las alertas');
            }
        };

        fetchAlerts();
    }, [setUnreadCount]);

    return (
        <div className="notifications-container">
            <h2>Notificaciones</h2>
            {error && <p className="error-message">{error}</p>}
            {!alert && !error && <p>No hay alertas disponibles</p>}
            {alert && (
                <div className="notification-item">
                    <p><strong>Mensaje:</strong> {alert.mensaje}</p>
                    <p><strong>IP:</strong> {alert.ip}</p>
                    <p><strong>Ubicación:</strong></p>
                    <ul>
                        <li><strong>Ciudad:</strong> {alert.ubicacion.ciudad}</li>
                        <li><strong>País:</strong> {alert.ubicacion.pais}</li>
                        <li><strong>Región:</strong> {alert.ubicacion.region}</li>
                        <li><strong>Latitud:</strong> {alert.ubicacion.latitud}</li>
                        <li><strong>Longitud:</strong> {alert.ubicacion.longitud}</li>
                    </ul>
                </div>
            )}
            {/* Toolbar */}
            <Toolbar onNavigate={navigate} />
        </div>
    );
}

export default Notifications;
