import React, { useContext, useEffect } from 'react';
import { FaHome, FaBell, FaCamera } from 'react-icons/fa';
import './../styles.css';
import { NotificationsContext } from '../context/NotificationsContext';

function Toolbar({ onNavigate }) {
  const { unreadCount, setUnreadCount } = useContext(NotificationsContext); // Acceder al contexto

  useEffect(() => {
    // Función para obtener las alertas desde el servicio
    const fetchAlerts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:9000/alertas');
        if (!response.ok) {
          throw new Error('Error al obtener las alertas');
        }
        const data = await response.json();

        if (data.success && Array.isArray(data.data)) {
          // Filtrar las alertas con datos de ubicación válidos
          const validAlerts = data.data.filter(alert =>
            alert.ubicacion &&
            alert.ubicacion.ciudad &&
            alert.ubicacion.latitud &&
            alert.ubicacion.longitud &&
            alert.ubicacion.pais &&
            alert.ubicacion.region
          );

          // Eliminar duplicados basados en mensaje e IP
          const uniqueAlerts = Array.from(
            new Set(validAlerts.map(alert => `${alert.mensaje}-${alert.ip}`))
          );

          // Actualizar el contador de notificaciones no leídas
          setUnreadCount(uniqueAlerts.length);
        }
      } catch (err) {
        console.error('Error al obtener las alertas:', err);
      }
    };

    // Configurar el polling para verificar nuevas alertas cada 10 segundos
    const interval = setInterval(fetchAlerts, 10000);

    // Limpiar el intervalo al desmontar el componente
    return () => clearInterval(interval);
  }, [setUnreadCount]);

  // Función para manejar el clic en el ícono de notificaciones
  const handleNotificationsClick = () => {
    setUnreadCount(0); // Reiniciar el contador de notificaciones no leídas
    onNavigate('/notifications'); // Navegar a la página de notificaciones
  };

  return (
    <div className="toolbar">
      <div className="toolbar-item" onClick={() => onNavigate('/dashboard')}>
        <FaHome size={24} />
        <p>Inicio</p>
      </div>
      <div className="toolbar-scan" onClick={() => onNavigate('/scan')}>
        <FaCamera size={24} />
      </div>
      <div
        className="toolbar-item"
        onClick={handleNotificationsClick}
        style={{ position: 'relative' }}
      >
        <FaBell size={24} />
        {unreadCount > 0 && (
          <span className="notification-badge">
            {unreadCount}
          </span>
        )}
        <p>Notificaciones</p>
      </div>
    </div>
  );
}

export default Toolbar;
