import React, { useEffect, useState } from 'react';
import './../styles.css'; // Archivo de estilos para el componente
import Toolbar from './Toolbar';
import { useNavigate } from 'react-router-dom'; // Importar useNavigate para la navegación

function Notifications() {
  const [alerts, setAlerts] = useState([]); // Estado para almacenar las alertas
  const [error, setError] = useState(''); // Estado para manejar errores

  useEffect(() => {
    // Función para obtener las alertas desde el servicio
    const fetchAlerts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/alertas');
        if (!response.ok) {
          throw new Error('Error al obtener las alertas');
        }
        const data = await response.json();
        setAlerts(data); // Almacena las alertas en el estado
      } catch (err) {
        console.error('Error al obtener las alertas:', err);
        setError('No se pudieron cargar las alertas');
      }
    };

    fetchAlerts();
  }, []);

  return (
    <div className="notifications-container">
      <h2>Notificaciones</h2>
      {error && <p className="error-message">{error}</p>}
      {alerts.length === 0 && !error && <p>No hay alertas disponibles</p>}
      <ul className="notifications-list">
        {alerts.map((alert, index) => (
          <li key={index} className="notification-item">
            <p><strong>Mensaje:</strong> {alert.mensaje}</p>
            <p><strong>IP:</strong> {alert.ip}</p>
            <p><strong>Ubicación:</strong></p>
            <ul>
              <li><strong>Ciudad:</strong> {alert.ubicacion.ciudad || 'No disponible'}</li>
              <li><strong>País:</strong> {alert.ubicacion.pais || 'No disponible'}</li>
              <li><strong>Región:</strong> {alert.ubicacion.region || 'No disponible'}</li>
              <li><strong>Latitud:</strong> {alert.ubicacion.latitud || 'No disponible'}</li>
              <li><strong>Longitud:</strong> {alert.ubicacion.longitud || 'No disponible'}</li>
            </ul>
          </li>
        ))}
      </ul>
      {/* Toolbar */}
      <Toolbar onNavigate={navigate} />
    </div>
  );
}

export default Notifications;
