import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Html5QrcodeScanner } from 'html5-qrcode';
import './../styles.css'; // Archivo de estilos para el componente
import Toolbar from './Toolbar';

function ScanQR() {
  const [qrData, setQrData] = useState(null); // Estado para almacenar los datos del QR
  const navigate = useNavigate();
  let scannerInstance = null; // Declarar el escáner fuera del `useEffect`

  useEffect(() => {
    // Limpia el contenedor antes de inicializar el escáner
    const readerElement = document.getElementById('reader');
    if (readerElement) {
      readerElement.innerHTML = ''; // Limpia cualquier contenido previo
    }

    // Inicializa el escáner
    scannerInstance = new Html5QrcodeScanner(
      'reader', // ID del contenedor donde se renderiza el escáner
      { fps: 10, qrbox: 250 }, // Configuración del escáner
      false
    );

    scannerInstance.render(
      (decodedText) => {
        if (decodedText) {
          setQrData(decodedText); // Almacena los datos escaneados
          if (decodedText === '10') {
            // Limpia el escáner antes de redirigir
            scannerInstance.clear().then(() => {
              console.log('Escáner limpiado correctamente antes de redirigir.');
              navigate('/dashboard', { state: { pointsToAdd: 10 } }); // Redirige al Dashboard con los puntos
            }).catch((error) => {
              console.error('Error al limpiar el escáner antes de redirigir:', error);
            });
          }
        }
      },
      (error) => {
        console.error('Error al escanear el código QR:', error);
      }
    );

    // Limpia el escáner al desmontar el componente
    return () => {
      if (scannerInstance) {
        scannerInstance.clear().then(() => {
          console.log('Escáner limpiado correctamente al desmontar.');
        }).catch((error) => {
          console.error('Error al limpiar el escáner al desmontar:', error);
        });
      }
    };
  }, [navigate]);

  return (
    <div className="scan-qr-container">
      <h2>Escanear Código QR</h2>
      <div id="reader" style={{ width: '100%' }}></div>
      {qrData && (
        <div className="qr-result">
          <h3>Datos del QR:</h3>
          <p>{qrData}</p>
        </div>
      )}
      {/* Toolbar */}
      <Toolbar onNavigate={navigate} />
    </div>
  );
}

export default ScanQR;
