import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import Toolbar from './Toolbar'; // Importar el Toolbar
import './../styles.css'; // Archivo de estilos para el Dashboard

function Dashboard() {
  const navigate = useNavigate();

  // Estados para los puntos y el nivel
  const [points, setPoints] = useState(0); // Puntos actuales
  const [level, setLevel] = useState(0); // Nivel actual
  const maxPoints = 100; // Máximo de puntos para subir de nivel

  const handleLogout = () => {
    navigate('/');
  };

  // Función para añadir puntos
  const addPoints = () => {
    setPoints((prevPoints) => {
      const newPoints = prevPoints + 10;
      if (newPoints >= maxPoints) {
        // Si los puntos alcanzan o superan el máximo, sube de nivel
        setLevel((prevLevel) => prevLevel + 1);
        return newPoints - maxPoints; // Resta los puntos sobrantes al nuevo nivel
      }
      return newPoints;
    });
  };

  return (
    <div className="dashboard-container">
      <h2>Bienvenido</h2>
      <div style={{ width: 200, margin: '20px auto' }}>
        <CircularProgressbar
          value={(points / maxPoints) * 100} // Calcula el porcentaje basado en los puntos actuales
          text={`Nivel ${level}`} // Muestra el nivel actual
          styles={buildStyles({
            textColor: '#000',
            pathColor: '#3b82f6',
            trailColor: '#d6d6d6',
          })}
        />
      </div>
      <p>Puntos acumulados: {points} / {maxPoints}</p>
      <p>Nivel actual: {level}</p>
      <button onClick={addPoints} style={{ marginRight: '10px' }}>
        Añadir Puntos
      </button>
      <button onClick={handleLogout}>Cerrar Sesión</button>

      {/* Toolbar */}
      <Toolbar onNavigate={navigate} />
    </div>
  );
}

export default Dashboard;
