import React, { useContext, useEffect } from 'react';
import { FaHome, FaBell, FaCamera } from 'react-icons/fa';
import './../styles.css';
import { NotificationsContext } from '../context/NotificationsContext';

function Toolbar({ onNavigate }) {
  const { unreadCount, setUnreadCount } = useContext(NotificationsContext); // Acceder al contexto

  useEffect(() => {
    // Función para obtener las alertas no leídas desde el servicio
    const fetchUnreadAlerts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:9000/alertas');
        if (!response.ok) {
          throw new Error('Error al obtener las alertas');
        }
        const data = await response.json();

        if (data.success && Array.isArray(data.data)) {
          // Actualizar el contador de notificaciones no leídas
          setUnreadCount(data.data.length);
        }
      } catch (err) {
        console.error('Error al obtener las alertas:', err);
      }
    };

    // Configurar el polling para verificar nuevas alertas cada 10 segundos
    const interval = setInterval(fetchUnreadAlerts, 10000);

    // Limpiar el intervalo al desmontar el componente
    return () => clearInterval(interval);
  }, [setUnreadCount]);

  // Función para manejar el clic en el ícono de notificaciones
  const handleNotificationsClick = async () => {
    try {
      // Marcar todas las alertas como leídas
      await fetch('http://127.0.0.1:9000/api/alertas/marcar-leidas', {
        method: 'PUT',
      });

      // Reiniciar el contador de notificaciones no leídas
      setUnreadCount(0);

      // Navegar a la página de notificaciones
      onNavigate('/notifications');
    } catch (err) {
      console.error('Error al marcar las alertas como leídas:', err);
    }
  };

  return (
    <div className="toolbar">
      <div className="toolbar-item" onClick={() => onNavigate('/dashboard')}>
        <FaHome size={24} />
        <p>Inicio</p>
      </div>
      <div className="toolbar-scan" onClick={() => onNavigate('/scan')}>
        <FaCamera size={24} />
      </div>
      <div
        className="toolbar-item"
        onClick={handleNotificationsClick}
        style={{ position: 'relative' }}
      >
        <FaBell size={24} />
        {unreadCount > 0 && (
          <span className="notification-badge">
            {unreadCount}
          </span>
        )}
        <p>Notificaciones</p>
      </div>
    </div>
  );
}

export default Toolbar;
