import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Html5QrcodeScanner } from 'html5-qrcode';
import './../styles.css'; // Archivo de estilos para el componente

function ScanQR() {
  const [qrData, setQrData] = useState(null); // Estado para almacenar los datos del QR
  const navigate = useNavigate();

  useEffect(() => {
    // Verifica si el contenedor ya tiene un escáner activo y lo limpia
    const readerElement = document.getElementById('reader');
    if (readerElement) {
      readerElement.innerHTML = ''; // Limpia el contenedor antes de inicializar el escáner
    }

    const scanner = new Html5QrcodeScanner(
      'reader', // ID del contenedor donde se renderiza el escáner
      { fps: 10, qrbox: 250 }, // Configuración del escáner
      false
    );

    scanner.render(
      (decodedText) => {
        setQrData(decodedText); // Almacena los datos escaneados
        if (decodedText === '10') {
          navigate('/dashboard', { state: { pointsToAdd: 10 } }); // Redirige al Dashboard con los puntos
        }
      },
      (error) => {
        console.error('Error al escanear el código QR:', error);
      }
    );

    // Limpia el escáner al desmontar el componente
    return () => {
      scanner.clear().catch((error) => {
        console.error('Error al limpiar el escáner:', error);
      });
    };
  }, [navigate]);

  return (
    <div className="scan-qr-container">
      <h2>Escanear Código QR</h2>
      <div id="reader" style={{ width: '100%' }}></div>
      {qrData && (
        <div className="qr-result">
          <h3>Datos del QR:</h3>
          <p>{qrData}</p>
        </div>
      )}
    </div>
  );
}

export default ScanQR;
