import React, { useContext } from 'react';
import { FaHome, FaBell, FaCamera } from 'react-icons/fa';
import './../styles.css';
import { NotificationsContext } from '../context/NotificationsContext';

function Toolbar({ onNavigate }) {
  const { unreadCount } = useContext(NotificationsContext); // Acceder al contexto

  return (
    <div className="toolbar">
      <div className="toolbar-item" onClick={() => onNavigate('/dashboard')}>
        <FaHome size={24} />
        <p>Inicio</p>
      </div>
      <div className="toolbar-scan" onClick={() => onNavigate('/scan')}>
        <FaCamera size={24} />
      </div>
      <div className="toolbar-item" onClick={() => onNavigate('/notifications')}>
        <FaBell size={24} />
        {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
        <p>Notificaciones</p>
      </div>
    </div>
  );
}

export default Toolbar;
